package types_test

import (
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bandprotocol/chain/v3/x/tunnel/types"
)

func TestStringRouterMemo(t *testing.T) {
	memo := types.NewRouterMemo(
		"router17c2txg2px6vna8a6v4ql4eh4ruvprerhytxvwt2ugp4qr473pajsyj9pgm",
		"17000",
		"0xDFCfEbF22e85193eDc37b8b136d4F3394987d1AE",
		300000,
		10000000,
		"",
		0,
		"",
	)
	memoStr, err := memo.String()
	require.NoError(t, err)
	require.Equal(
		t,
		`{"wasm":{"contract":"router17c2txg2px6vna8a6v4ql4eh4ruvprerhytxvwt2ugp4qr473pajsyj9pgm","msg":{"receive_band_data":{"dest_chain_id":"17000","dest_contract_address":"0xDFCfEbF22e85193eDc37b8b136d4F3394987d1AE","gas_limit":300000,"gas_price":10000000,"payload":"","nonce":0,"signature":""}}}}`,
		memoStr,
	)
}
